import styled from "@emotion/styled";
export const ContainerPhoneBook = styled.div`
  font-family: "Roboto";
  font-style: normal;

  width: 288px;
  padding: 23px;

  background: #ffffff;
  box-shadow: 0px 1px 4px rgba(126, 147, 255, 0.2);
  border-radius: 6px;
`;
export const TitlePhoneBook = styled.h1`
  font-weight: 600;
  font-size: 18px;
  line-height: 1.16;

  letter-spacing: 0.01em;
`;
