import { AiOutlineDelete } from "react-icons/ai";

const Contacts = ({ contacts, removeContact }) => {
  return (
    <>
      <h2>Results:</h2>
      <ul>
        {contacts.map(({ id, name, number }) => {
          return (
            <li key={id}>
              <div>
                <p>{name}</p>
                <p>{number}</p>
              </div>

              <button type="button" onClick={removeContact}>
                <AiOutlineDelete />
              </button>
            </li>
          );
        })}
      </ul>
    </>
  );
};
export default Contacts;
